mapper.update:
update neuedu_shipping



        receiver_name = #{receiver_name}


        receiver_phone = #{receiver_phone}


        receiver_mobile = #{receiver_mobile}


        receiver_province = #{receiver_province}


        receiver_city = #{receiver_city}


        receiver_district = #{receiver_district}


        receiver_address = #{receiver_address}


        receiver_zip = #{receiver_zip}


        update_time = #{update_time}




        id = #{id}

mapper.select:
select id,user_id, receiver_name, receiver_phone, receiver_mobile,receiver_province,receiver_city,receiver_district, receiver_address,receiver_zip,create_time,update_time from neuedu_shipping where user_id=#{userid}
order by receiver_name asc
ORDER by receiver_name desc
limit #{offset},#{pageSize}

select id from neuedu_shipping
where user_id=#{user_id}
and receiver_name=#{receiver_name}
and receiver_address=#{receiver_address}
and receiver_mobile=#{receiver_mobile}
